mapper.select:
SELECT b.BUG_ID AS bugId, b.BUG_NAME AS bugName, b.SYSTEM_ID AS systemId, b.BUG_STATE AS bugState, bs.BUG_STATE_NAME AS bugStateName, b.USECASE_ID AS usecaseId, b.BUG_SOURCE AS bugSource, dic_source.DATA_NAME AS bugSourceName, b.PRORITY AS prority, dic_priority.DATA_NAME AS prorityName, b.BUG_DESCRIPITION AS bugDescription, b.BUG_SEVERITY_LEVEL AS bugSeverityLevel, dic_severity.DATA_NAME AS bugSeverityLevelName, b.CLOSE_REASON AS closeReason, b.SUBMITTER_ID AS submitterId, submitter.USER_NAME AS submitterName, b.DEVELOPER_ID AS developerId, developer.USER_NAME AS developerName, b.DEV_LEADER_ID AS devLeaderId, dev_leader.USER_NAME AS devLeaderName, b.CHECKER_ID AS checkerId, checker.USER_NAME AS checkerName, b.BUG_TYPE AS bugType, dic_type.DATA_NAME AS bugTypeName, b.DIRECTORY_ID AS directoryId, b.COMMIT_TIME AS commitTime, b.CONFIRMED_TIME AS confirmedTime, b.ASSIGNED_TIME AS assignedTime, b.RESOLVED_TIME AS resolvedTime, b.WAITCHECK_TIME AS waitCheckTime, b.CLOSE_TIME AS closeTime, b.SOLVE_VOLUME AS solveVolume, b.SUBMITTED_VOLUME AS submittedVolume, b.BUG_WORKFLOW_ID AS bugWorkflowId FROM tf_bug b LEFT JOIN t_sys_user submitter ON b.SUBMITTER_ID = submitter.USER_ID LEFT JOIN t_sys_user developer ON b.DEVELOPER_ID = developer.USER_ID LEFT JOIN t_sys_user dev_leader ON b.DEV_LEADER_ID = dev_leader.USER_ID LEFT JOIN t_sys_user checker ON b.CHECKER_ID = checker.USER_ID LEFT JOIN tf_bug_state bs ON bs.BUG_STATE_CODE = b.BUG_STATE LEFT JOIN data_dictionary dic_source ON dic_source.DATA_TYPE = 'bugSource' AND dic_source.DATA_VALUE = b.BUG_SOURCE LEFT JOIN data_dictionary dic_priority ON dic_priority.DATA_TYPE = 'prority' AND dic_priority.DATA_VALUE = b.PRORITY LEFT JOIN data_dictionary dic_severity ON dic_severity.DATA_TYPE = 'bugSeverityLevel' AND dic_severity.DATA_VALUE = CAST(b.BUG_SEVERITY_LEVEL AS CHAR) LEFT JOIN data_dictionary dic_type ON dic_type.DATA_TYPE = 'bugType' AND dic_type.DATA_VALUE = b.BUG_TYPE WHERE 1 = 1
AND b.USECASE_ID = #{usecaseId}
AND CAST(b.DIRECTORY_ID AS CHAR) = #{directoryId}
ORDER BY b.COMMIT_TIME DESC
