mapper.select:
SELECT ur.REQUIRE_POINT_ID
FROM tf_usecase_require ur
WHERE ur.USECASE_ID = #{usecaseId}

mapper.delete:
DELETE FROM tf_usecase_require
WHERE USECASE_ID = #{usecaseId}

    AND REQUIRE_POINT_ID IN

        #{requirePointId}

DELETE FROM tf_usecase_require
WHERE REQUIRE_POINT_ID = #{requirePointId}

    AND USECASE_ID IN

        #{usecaseId}

mapper.select:
SELECT ur.USECASE_ID
FROM tf_usecase_require ur
WHERE ur.REQUIRE_POINT_ID = #{requirePointId}
